import React from 'react';
// import Img from '../img/IMG20220726114158.jpg';
import LogoGmail from '../img/logo_gmail.png';
import Contrast from '../img/contrast-adjustment.png';
// import GmailLogo from '../img/gmail-logo.png';
import Grid from '../img/grid.png';
import Help from '../img/help.png';
import Search from '../img/search.png';
import Settings from '../img/settings.png';
import Menu from '../img/menu.png';
// import './Email.css';
import './query.css';
import'./component/Email.css'


const Header=()=>{
    const onClick =()=>{
        const menu =()=>{
            document.querySelector('#menu');
        };
        const sidebar = document.querySelector('#side_bar');
        menu(sidebar.classList.toggle('show_sidebar'));
    };
    return(
        <div>
            <header>
                <nav>
                    <div className='navbar'>
                        <div className='flex1'>
                            <div className='menu_logo'>
                                <button style={{
                                    background: 'none',
                                    border:'none',
                                    cursor: 'pointer',
                                }} onClick={onClick}>
                                    <img id = 'menu' src={Menu} alt='' className='menu'/>
                                </button>
                                <a href='#!'>
                                    <img src={LogoGmail} alt = ''/>
                                </a>
                            </div>
                            <div className='search_bar'>
                                <div className='form'>
                                    <button type='submit' className='search_btn'>
                                        <img src ={Search} alt =''/>
                                    </button>
                                    <input type ='text' className='search_bar_input' placeholder='Search mail'/>
                                    <button type='submit' className='adjust_btn'>
                                        <img src={Contrast} alt = ''/>
                                        </button>
                                </div>
                            </div>
                            </div>
                            <div className='tools'>
                                <a href ='#!'>
                                    <img src={Help} alt = '' width = '24'/>
                                </a>
                                <a href ='#!'>
                                    <img src={Settings} alt = '' width = '24'/>
                                </a>
                                <a href ='#!'>
                                    <img src={Grid} alt = '' width = '24'/>
                                </a>
                                <a href ='#!'>
                                    <img src={Settings} alt = '' width = '35' height='35'
                                    style={{borderRadius: '50px'}}/>
                                </a>
                                </div>
                                </div>
                </nav>
            </header>
        </div>

    )
}
export default Header;
